import { Component, OnInit } from '@angular/core';
import { LessonPackage } from './lessonPackage.interface';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  ngOnInit(){
    console.log('AppComponent.ngOnInit()');
  }
  constructor() {
    console.log('AppComponent.constructor()');
  }
  title = 'tp3final';
  lessonPackage: LessonPackage = {
    title: '',
    description: '',
    category: '',
    level: '',
    prerequisite: [],
    tags: [],
    copyright: ''
  };
}


<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href = "#">Navbar</a>
  <a routerLink="/">Home</a>
  <ul class="navbar-nav mr-auto">
    <li ngbDropdown class="nav_item">
      <a ngbDropdownToggle id="navbarMenu1" class="nav-link dropdown-toggle" role="button">Menu1
      </a>
      <div ngbDropdownMenu aria-labelledby="navbarMenu2" class="dropdownmenu">
        <a class="dropdown-item" routerLink="/lesson-search-page">Lesson Search Page...</a>
      </div>
    </li>
    <li ngbDropdown class="nav_item">
      <a ngbDropdownToggle id="navbarMenu2" class="nav-link dropdown-toggle" role="button">Menu2
      </a>
      <div ngbDropdownMenu aria-labelledby="navbarMenu2" class="dropdownmenu">
        <a class="dropdown-item" routerLink="/lesson-edit-form">Lesson Edit
          Form...</a>
      </div>
    </li>
  </ul>

</nav>


